package test;
import java.util.*;
public class EvOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		if(((n % 2)) == 0){
			System.out.println(1);
		}
		else {
			System.out.println(0);
		}
	}

}